import React, {Component,PropTypes} from 'react';
import ReactDOM from 'react-dom';
import classNames from 'classnames';
import {Link} from 'react-router';

import {
  Page,
  Wrapper,
  Flex, FlexItem,
  Toasts,
  Button,
} from '../../components/index';

export default class ToastTest extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      toast: {
        message: 'Boom!!!',
        open: false,
        icon: '',
        gif: '',
      },
    }

    this.handleToastOnlyWord = this.handleToastOnlyWord.bind(this);
    this.handleToastNoWord = this.handleToastNoWord.bind(this);
    this.handleToastHasIcon = this.handleToastHasIcon.bind(this);
    this.handleToastHasGif = this.handleToastHasGif.bind(this);
  }

  // 只有文字情况
  handleToastOnlyWord() {
    this.setState({
      toast: {
        message: 'Boom!!!',
        open: true,
      },
    });
  }

  // 没有文字情况
  handleToastNoWord() {
    this.setState({
      toast: {
        message: '',
        open: true,
        icon: 'icon-dagou2'
      },
    });
  }

  // 带Icon的情况
  handleToastHasIcon() {
    this.setState({
      toast: {
        message: 'Boom!!!',
        open: true,
        icon: 'icon-dagou2'
      },
    });
  }

  // 带Gif的情况
  handleToastHasGif() {
    this.setState({
      toast: {
        message: 'Boom!!!',
        open: true,
        gif: 'data:image/gif;base64,'
      },
    });
  }

  render() {
    const { toast } = this.state;

    return (
      <Page>
        <Wrapper>
          <Flex>
            <FlexItem>
              <Button className="btn-primary" onClick={this.handleToastOnlyWord}>文字</Button>
            </FlexItem>
            <FlexItem>
              <Button className="btn-primary" onClick={this.handleToastHasIcon}>带Icon</Button>
            </FlexItem>
          </Flex>
        </Wrapper>
        <Wrapper>
          <Flex>
            <FlexItem>
              <Button className="btn-primary" onClick={this.handleToastNoWord}>没有文字</Button>
            </FlexItem>
            <FlexItem>
              <Button className="btn-primary" onClick={this.handleToastHasGif}>带Gif</Button>
            </FlexItem>
          </Flex>
        </Wrapper>
        <Toasts
          icon={toast.icon}
          open={toast.open}
          gif={toast.gif}
          message={toast.message}
        />
      </Page>
    );
  }
}